import {Component} from '@angular/core';
import {FileOpener} from '@capacitor-community/file-opener';
import {Directory, Filesystem} from "@capacitor/filesystem";

@Component({
  selector: 'app-tab1',
  templateUrl: 'tab1.page.html',
  styleUrls: ['tab1.page.scss']
})
export class Tab1Page {

  private tst = "data:image/png;base64,"

  constructor() {}


  public async testMethod(): Promise<void> {
  
	this.getFilesystemAccess();
  
    try {
      const result = await
        Filesystem
          .writeFile({
            data: this.tst,
            path: "testxxx.png",
            directory: Directory.Documents
            ,
            recursive: true
          })
      await FileOpener.open({filePath: result.uri});
    } catch (e) {
      console.log('Error opening file', e);
    }
  }
  
  private getFilesystemAccess(): Promise<boolean> {
        return new Promise(async (resolve) => {
            const status = await Filesystem.checkPermissions()
            const state = status.publicStorage

            if (state === 'granted') {
                return resolve(true)
            } else if (state === 'denied') {
                return false;
            } else {
                let result = await Filesystem.requestPermissions();
                return result.publicStorage === "granted";
            }
        })
    }
}
